import { Component, OnInit } from "@angular/core";
import { Router } from "@angular/router";
import { ApiService } from "src/app/Services/api.service";

@Component({
  selector: "app-order-page",
  templateUrl: "./order-page.component.html",
  styleUrls: ["./order-page.component.css"],
})
export class OrderPageComponent implements OnInit {
  public finalamount: number = 0;
  public totalamount: number = 0;
  public userdata: any;
  public username: any;
  constructor(private api: ApiService, private router: Router) {}
  ngOnInit(): void {
    setTimeout(() => {
      this.router.navigate(["/"]);
      this.api.removeallitems();
    }, 4000);

    // totalamount coming from api
    this.totalamount = this.api.calculateprice();

    // recieving data between components getting data
    this.finalamount = this.api.recievefinalamount();
    //alert(this.data)

    //getting item from localstorage
    let localdata = localStorage.getItem("ecomdata");
    this.userdata = localdata;
    this.username = JSON.parse(this.userdata);
  }
}
<div class="card">
  <div
    style="
      border-radius: 200px;
      height: 200px;
      width: 200px;
      background: #f8faf5;
      margin: 0 auto;
    "
  >
    <img
      style="width: 188px"
      src="https://cdn.pixabay.com/photo/2017/01/13/01/22/ok-1976099_1280.png"
    />
  </div>
  <h1 style="text-align: center; color: crimson">{{ username | titlecase }}</h1>
  <h2>Your Order has Placed Successfully</h2>
  <strong style="margin-left: 45px"
    >Thankyou For Shopping With popular E-commerce</strong
  >
  <h5 style="margin-top: 20px; margin-left: 70px; color: green">
    Kindly Pay Amount
    <strong style="color: blue">${{ finalamount | number : "1.0-0" }}</strong
    >&nbsp;during dilivery
  </h5>
</div>
