// product-view.component.html
<div class="container-fluid" style="background-color: #ecf0f1;">
  <div class="d-flex justify-content-center mt-3">
    <app-searchbar (search)="searchProducts($event)"></app-searchbar>
  </div>
  <app-baner></app-baner>
  
  <div class="row">
    <div class="col-md-3 mt-3" *ngFor="let item of data?.products">
      <div class="card mt-2">
        <div class="card-body">
          <div class="card-img-actions">
            <img routerLink="/product-detail/{{ item.id }}" src="{{ item.thumbnail }}" class="card-img img-fluid" />
          </div>
        </div>

        <div class="card-body bg-light text-center">
          <div>
            <h6 class="font-weight-semibold">
              <a style="font-size: 13px" class="text-default">{{ item.title | slice : 0 : 18 }}</a>
            </h6>
          </div>
          <h3 class="mb-0 font-weight-semibold">$ {{ item.price }}</h3>
          <button routerLink="/product-detail/{{ item.id }}" class="btn btn-sm btn-dark mt-2">
            <i class="fa fa-solid fa-info mr-2"></i>&nbsp;&nbsp;View Details
          </button>
          <button (click)="addtocart(item)" class="btn btn-sm btn-warning mt-2">
            <i class="fa fa-cart-plus mr-2"></i>&nbsp;Add to Cart
          </button>
          <button (click)="removeitem(item)" class="btn btn-sm btn-danger mt-2">
            <i class="fa fa-remove mr-2"></i>&nbsp;Remove Item
          </button>
        </div>
      </div>
    </div>
  </div>
</div>
//Product-View.Component.ts
import { Component, OnInit } from "@angular/core";
import { ApiService } from "src/app/Services/api.service";
import { product } from "./productmodal";

@Component({
  selector: "app-product-view",
  templateUrl: "./product-view.component.html",
  styleUrls: ["./product-view.component.css"],
})
export class ProductViewComponent implements OnInit {
  data: any | product[];
  item: any;
  constructor(private api: ApiService) {}
  ngOnInit(): void {
    this.displayproducts();
    localStorage.removeItem("ecomdata");
  }
  displayproducts() {
    this.api.getproduct().subscribe((res) => {
      this.data = res;
      console.log(res);
    });
  }
  addtocart(item: product) {
    this.api.addtocart(item);
  }

  removeitem(item: product) {
    this.api.removecartitem(item);
  }

   searchProducts(keyword: string) {
    if (keyword.trim() !== '') {
      this.api.searchProduct(keyword).subscribe((res) => {
        this.data = res;
      });
    } else {
      // If the search bar is empty, display all products
      this.displayproducts();
    }
  }
}
