import { Component } from '@angular/core';

@Component({
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.css']
})
export class SidebarComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Unique Sidebar Design</title>

  <!-- Add Bootstrap CSS (optional, for styling) -->
  <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css">
  
  <style>
    /* Custom styles for the sidebar */
    .custom-sidebar {
      background-color: #ecf0f1; /* Set the background color */
      color: #2c3e50; /* Change text color as needed */
      border-radius: 10px;
      padding-bottom: 50px; /* Set the height of the background color at the end */
    }

    .custom-sidebar .list-group-item {
      border: none;
      background-color: transparent;
      transition: background-color 0.3s;
    }

    .custom-sidebar .list-group-item:hover {
      background-color: #d0d3d4; /* Change hover background color as needed */
    }

    .custom-sidebar a {
      text-decoration: none;
    }

    /* Style for the background color at the end */
    .sidebar-background {
      position: absolute;
      bottom: 0;
      left: 0;
      width: 100%;
      height: 50px; /* Match the padding-bottom value */
      background-color:  #ecf0f1; /* Change the color as needed */
    }
  </style>
</head>
<body style="position: relative;"> <!-- Add relative positioning to the body -->

  <!-- Unique Sidebar -->
  <div class="list-group custom-sidebar mx-3 mt-4">
    <li class="list-group-item py-2">
      <a routerLink="/" class="text-reset">Product</a>
    </li>
    <li class="list-group-item py-2">
      <a href="#" class="text-reset">Services</a>
    </li>
    <li class="list-group-item py-2">
      <a href="#" class="text-reset">About us</a>
    </li>
    <li class="list-group-item py-2">
      <a href="#" class="text-reset">Contact</a>
    </li>
  </div>


  <!-- Rest of your content -->

  <!-- Bootstrap JS and Popper.js (optional, for Bootstrap functionality) -->
  <script src="https://code.jquery.com/jquery-3.2.1.slim.min.js"></script>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/popper.js/1.12.9/umd/popper.min.js"></script>
  <script src="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/js/bootstrap.min.js"></script>
</body>
</html>
