import { Component } from '@angular/core';
import { ApiService } from 'path-to-your-api-service';

@Component({
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.css']
})
export class SidebarComponent {
  categories: string[] = [
    "smartphones",
    "laptops",
    "fragrances",
    "skincare",
    "groceries",
    "furniture",
    "automotive"
  ];

  constructor(private apiService: ApiService) {}

  getProducts(category: string): void {
    // Call the API service method to get products by category
    this.apiService.getProductsByCategory(category).subscribe(
      (products) => {
        // Handle the returned products as needed, e.g., update a product list in the component
        console.log(`Products for category ${category}:`, products);
      },
      (error) => {
        console.error('Error fetching products:', error);
      }
    );
  }
}
