import { Component } from '@angular/core';

@Component({
  selector: 'app-baner',
  templateUrl: './baner.component.html',
  styleUrls: ['./baner.component.css']
})
export class BanerComponent {

  constructor(){
    
  }

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>E-commerce Banner</title>

  <!-- Add Bootstrap CSS (optional, for styling) -->
  <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css">
  
  <style>
    /* Custom styles for the banner */
    .banner {
      position: relative;
      height: 300px; /* Adjust the height as needed */
      background-image: url('https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRzFEisjzgMl1-4NvukvoUoU_ta6pCempxXGBpSi2iTJN62aKRdLGdzWqBy-SNR9PMjZu4&usqp=CAU'); /* Replace with your actual image URL */
      background-size: cover;
      background-position: center;
      color: #fff;
      text-align: center;
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;
    }

    .banner h1 {
      font-size: 2.5rem;
      margin-bottom: 10px;
    }

    .banner p {
      font-size: 1.2rem;
    }
  </style>
</head>
<body>

  <!-- Banner with E-commerce Image -->
  <div class="banner">
    <h1>Welcome to Our E-commerce Store</h1>
    <p>Discover Amazing Deals and Shop Now!</p>
  </div>

  <!-- Rest of your content -->

  <!-- Bootstrap JS and Popper.js (optional, for Bootstrap functionality) -->
  <script src="https://code.jquery.com/jquery-3.2.1.slim.min.js"></script>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/popper.js/1.12.9/umd/popper.min.js"></script>
  <script src="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/js/bootstrap.min.js"></script>
</body>
</html>
